$.ajax({url:"https://personal-site-60774.firebaseio.com/projectsArray.json"}).then(a=>{const n=a;let e=document.getElementById("portfolio");n.forEach(a=>{e.innerHTML+=`\n            <article class="col-sm-6">\n                <div class="thumbnail">\n                    <a href="${a.image}" target="_blank"><img src="${a.image}" class="img-responsive" alt="${a.name}">\n                        <div class="caption">\n                        <h4>${a.name}</h4>\n                        <p><span class="important-text">Date completed:</span> ${a.date} &nbsp; &nbsp; <span class="important-text">Teammates:</span> ${a.teammates}</p>\n                        <p><span class="important-text">Technologies used:</span> ${a.technologies}</p>\n                        </div>\n                    </a>\n                </div>\n            </article>\n        `})});